				/* ]]> */
			</script>
			<h3 class="tb"><label for="this_photo"><?php _e('URL') ?></label></h3>
			<div class="titlediv">
				<div class="titlewrap">
					<input id="this_photo" name="this_photo" class="tbtitle text" onkeypress="if(event.keyCode==13) image_selector();" />
