                $access_token_response = $this->_oauthRequest(
                    $this->getUrl('graph', '/oauth/access_token'),
                    $params = array(
                        'client_id' => $this->getAppId(),
                        'client_secret' => $this->getAppSecret(),
                        'grant_type' => 'fb_exchange_token',
                        'fb_exchange_token' => $this->getAccessToken(),
                    )
                );
            }
            catch (FacebookApiException $e) {
                // most likely that user very recently revoked authorization.
                // In any event, we don't have an access token, so say so.
                return false;
            }

            if (empty($access_token_response)) {
                return false;
            }

            $response_params = array();
            parse_str($access_token_response, $response_params);

            if (!isset($response_params['access_token'])) {
                return false;
            }

            $this->destroySession();

            $this->setPersistentData(
                'access_token', $response_params['access_token']
            );
        }

        /**
         * Determines the access token that should be used for API calls.
         * The first time this is called, $this->accessToken is set equal
         * to either a valid user access token, or it's set to the application
         * access token if a valid user access token wasn't available.  Subsequent
         * calls return whatever the first call returned.
         *
         * @return string The access token
         */
        public function getAccessToken() {
            if ($this->accessToken !== null) {
                // we've done this already and cached it.  Just return.
                return $this->accessToken;
            }

            // first establish access token to be the application
            // access token, in case we navigate to the /oauth/access_token
            // endpoint, where SOME access token is required.
            $this->setAccessToken($this->getApplicationAccessToken());
            $user_access_token = $this->getUserAccessToken();
            if ($user_access_token) {
                $this->setAccessToken($user_access_token);
            }

            return $this->accessToken;
        }

        /**
         * Determines and returns the user access token, first using
         * the signed request if present, and then falling back on
         * the authorization code if present.  The intent is to
         * return a valid user access token, or false if one is determined
         * to not be available.
         *
         * @return string A valid user access token, or false if one
         *                could not be determined.
         */
        protected function getUserAccessToken() {
            // first, consider a signed request if it's supplied.
            // if there is a signed request, then it alone determines
            // the access token.
            $signed_request = $this->getSignedRequest();
            if ($signed_request) {
                // apps.facebook.com hands the access_token in the signed_request
                if (array_key_exists('oauth_token', $signed_request)) {
                    $access_token = $signed_request['oauth_token'];
                    $this->setPersistentData('access_token', $access_token);
                    return $access_token;
                }

                // the JS SDK puts a code in with the redirect_uri of ''
                if (array_key_exists('code', $signed_request)) {
                    $code = $signed_request['code'];
                    if ($code && $code == $this->getPersistentData('code')) {
                        // short-circuit if the code we have is the same as the one presented
                        return $this->getPersistentData('access_token');
                    }

                    $access_token = $this->getAccessTokenFromCode($code, '');
                    if ($access_token) {
                        $this->setPersistentData('code', $code);
                        $this->setPersistentData('access_token', $access_token);
                        return $access_token;
                    }
                }

                // signed request states there's no access token, so anything
                // stored should be cleared.
                $this->clearAllPersistentData();
                return false; // respect the signed request's data, even
                // if there's an authorization code or something else
            }

            $code = $this->getCode();
            if ($code && $code != $this->getPersistentData('code')) {
                $access_token = $this->getAccessTokenFromCode($code);
                if ($access_token) {
                    $this->setPersistentData('code', $code);
                    $this->setPersistentData('access_token', $access_token);
                    return $access_token;
                }

                // code was bogus, so everything based on it should be invalidated.
                $this->clearAllPersistentData();
                return false;
            }

            // as a fallback, just return whatever is in the persistent
            // store, knowing nothing explicit (signed request, authorization
            // code, etc.) was present to shadow it (or we saw a code in $_REQUEST,
            // but it's the same as what's in the persistent store)
            return $this->getPersistentData('access_token');
        }

        /**
         * Retrieve the signed request, either from a request parameter or,
         * if not present, from a cookie.
         *
         * @return string the signed request, if available, or null otherwise.
         */
        public function getSignedRequest() {
            if (!$this->signedRequest) {
                if (!empty($_REQUEST['signed_request'])) {
                    $this->signedRequest = $this->parseSignedRequest(
                        $_REQUEST['signed_request']);
                } else if (!empty($_COOKIE[$this->getSignedRequestCookieName()])) {
                    $this->signedRequest = $this->parseSignedRequest(
                        $_COOKIE[$this->getSignedRequestCookieName()]);
                }
            }
            return $this->signedRequest;
        }

        /**
         * Get the UID of the connected user, or 0
         * if the Facebook user is not connected.
         *
         * @return string the UID if available.
         */
        public function getUser() {
            if ($this->user !== null) {
                // we've already determined this and cached the value.
                return $this->user;
            }

            return $this->user = $this->getUserFromAvailableData();
        }

        /**
         * Determines the connected user by first examining any signed
         * requests, then considering an authorization code, and then
         * falling back to any persistent store storing the user.
         *
         * @return integer The id of the connected Facebook user,
         *                 or 0 if no such user exists.
         */
        protected function getUserFromAvailableData() {
            // if a signed request is supplied, then it solely determines
            // who the user is.
            $signed_request = $this->getSignedRequest();
            if ($signed_request) {
                if (array_key_exists('user_id', $signed_request)) {
                    $user = $signed_request['user_id'];

                    if($user != $this->getPersistentData('user_id')){
                        $this->clearAllPersistentData();
                    }

                    $this->setPersistentData('user_id', $signed_request['user_id']);
                    return $user;
                }

                // if the signed request didn't present a user id, then invalidate
                // all entries in any persistent store.
                $this->clearAllPersistentData();
                return 0;
            }

            $user = $this->getPersistentData('user_id', $default = 0);
            $persisted_access_token = $this->getPersistentData('access_token');

            // use access_token to fetch user id if we have a user access_token, or if
            // the cached access token has changed.
            $access_token = $this->getAccessToken();
            if ($access_token &&
                $access_token != $this->getApplicationAccessToken() &&
                !($user && $persisted_access_token == $access_token)) {
                $user = $this->getUserFromAccessToken();
                if ($user) {
                    $this->setPersistentData('user_id', $user);
                } else {
                    $this->clearAllPersistentData();
                }
            }

            return $user;
        }

        /**
         * Get a Login URL for use with redirects. By default, full page redirect is
         * assumed. If you are using the generated URL with a window.open() call in
         * JavaScript, you can pass in display=popup as part of the $params.
         *
         * The parameters:
         * - redirect_uri: the url to go to after a successful login
         * - scope: comma separated list of requested extended perms
         *
         * @param array $params Provide custom parameters
         * @return string The URL for the login flow
         */
        public function getLoginUrl($params=array()) {
            $this->establishCSRFTokenState();
            $currentUrl = $this->getCurrentUrl();

            // if 'scope' is passed as an array, convert to comma separated list
            $scopeParams = isset($params['scope']) ? $params['scope'] : null;
            if ($scopeParams && is_array($scopeParams)) {
                $params['scope'] = implode(',', $scopeParams);
            }

            return $this->getUrl(
                'www',
                'dialog/oauth',
                array_merge(array(
                        'client_id' => $this->getAppId(),
                        'redirect_uri' => $currentUrl, // possibly overwritten
                        'state' => $this->state),
                    $params));
        }

        /**
         * Get a Logout URL suitable for use with redirects.
         *
         * The parameters:
         * - next: the url to go to after a successful logout
         *
         * @param array $params Provide custom parameters
         * @return string The URL for the logout flow
         */
        public function getLogoutUrl($params=array()) {
            return $this->getUrl(
                'www',
                'logout.php',
                array_merge(array(
                    'next' => $this->getCurrentUrl(),
                    'access_token' => $this->getUserAccessToken(),
                ), $params)
            );
        }

        /**
         * Get a login status URL to fetch the status from Facebook.
         *
         * The parameters:
         * - ok_session: the URL to go to if a session is found
         * - no_session: the URL to go to if the user is not connected
         * - no_user: the URL to go to if the user is not signed into facebook
         *
         * @param array $params Provide custom parameters
         * @return string The URL for the logout flow
         */
        public function getLoginStatusUrl($params=array()) {
            return $this->getUrl(
                'www',
                'extern/login_status.php',
                array_merge(array(
                    'api_key' => $this->getAppId(),
                    'no_session' => $this->getCurrentUrl(),
                    'no_user' => $this->getCurrentUrl(),
                    'ok_session' => $this->getCurrentUrl(),
                    'session_version' => 3,
                ), $params)
            );
        }

        /**
         * Make an API call.
         *
         * @return mixed The decoded response
         */
        public function api(/* polymorphic */) {
            $args = func_get_args();
            if (is_array($args[0])) {
                return $this->_restserver($args[0]);
            } else {
                return call_user_func_array(array($this, '_graph'), $args);
            }
        }

        /**
         * Constructs and returns the name of the cookie that
         * potentially houses the signed request for the app user.
         * The cookie is not set by the BaseFacebook class, but
         * it may be set by the JavaScript SDK.
         *
         * @return string the name of the cookie that would house
         *         the signed request value.
         */
        protected function getSignedRequestCookieName() {
            return 'fbsr_'.$this->getAppId();
        }

        /**
         * Constructs and returns the name of the coookie that potentially contain
         * metadata. The cookie is not set by the BaseFacebook class, but it may be
         * set by the JavaScript SDK.
         *
         * @return string the name of the cookie that would house metadata.
         */
        protected function getMetadataCookieName() {
            return 'fbm_'.$this->getAppId();
        }

        /**
         * Get the authorization code from the query parameters, if it exists,
         * and otherwise return false to signal no authorization code was
         * discoverable.
         *
         * @return mixed The authorization code, or false if the authorization
         *               code could not be determined.
         */
        protected function getCode() {
            if (isset($_REQUEST['code'])) {
                if ($this->state !== null &&
                    isset($_REQUEST['state']) &&
                    $this->state === $_REQUEST['state']) {

                    // CSRF state has done its job, so clear it
                    $this->state = null;
                    $this->clearPersistentData('state');
                    return $_REQUEST['code'];
                } else {
                    self::errorLog('CSRF state token does not match one provided.');
                    return false;
                }
            }

            return false;
        }

        /**
         * Retrieves the UID with the understanding that
         * $this->accessToken has already been set and is
         * seemingly legitimate.  It relies on Facebook's Graph API
         * to retrieve user information and then extract
         * the user ID.
         *
         * @return integer Returns the UID of the Facebook user, or 0
         *                 if the Facebook user could not be determined.
         */
        protected function getUserFromAccessToken() {
            try {
                $user_info = $this->api('/me');
                return $user_info['id'];
            } catch (FacebookApiException $e) {
                return 0;
            }
        }

        /**
         * Returns the access token that should be used for logged out
         * users when no authorization code is available.
         *
         * @return string The application access token, useful for gathering
         *                public information about users and applications.
         */
        protected function getApplicationAccessToken() {
            return $this->appId.'|'.$this->appSecret;
        }

        /**
         * Lays down a CSRF state token for this process.
         *
         * @return void
         */
        protected function establishCSRFTokenState() {
            if ($this->state === null) {
                $this->state = md5(uniqid(mt_rand(), true));
                $this->setPersistentData('state', $this->state);
            }
        }

        /**
         * Retrieves an access token for the given authorization code
         * (previously generated from www.facebook.com on behalf of
         * a specific user).  The authorization code is sent to graph.facebook.com
         * and a legitimate access token is generated provided the access token
         * and the user for which it was generated all match, and the user is
         * either logged in to Facebook or has granted an offline access permission.
         *
         * @param string $code An authorization code.
         * @return mixed An access token exchanged for the authorization code, or
         *               false if an access token could not be generated.
         */
        protected function getAccessTokenFromCode($code, $redirect_uri = null) {
            if (empty($code)) {
                return false;
            }

            if ($redirect_uri === null) {
                $redirect_uri = $this->getCurrentUrl();
            }

            try {
                // need to circumvent json_decode by calling _oauthRequest
                // directly, since response isn't JSON format.
                $access_token_response =
                    $this->_oauthRequest(
                        $this->getUrl('graph', '/oauth/access_token'),
                        $params = array('client_id' => $this->getAppId(),
                            'client_secret' => $this->getAppSecret(),
                            'redirect_uri' => $redirect_uri,
                            'code' => $code));
            } catch (FacebookApiException $e) {
                // most likely that user very recently revoked authorization.
                // In any event, we don't have an access token, so say so.
                return false;
            }

            if (empty($access_token_response)) {
                return false;
            }

            $response_params = array();
            parse_str($access_token_response, $response_params);
            if (!isset($response_params['access_token'])) {
                return false;
            }

            return $response_params['access_token'];
        }

        /**
         * Invoke the old restserver.php endpoint.
         *
         * @param array $params Method call object
         *
         * @return mixed The decoded response object
         * @throws FacebookApiException
         */
        protected function _restserver($params) {
            // generic application level parameters
            $params['api_key'] = $this->getAppId();
            $params['format'] = 'json-strings';

            $result = json_decode($this->_oauthRequest(
                $this->getApiUrl($params['method']),
                $params
            ), true);

            // results are returned, errors are thrown
            if (is_array($result) && isset($result['error_code'])) {
                $this->throwAPIException($result);
                // @codeCoverageIgnoreStart
            }
            // @codeCoverageIgnoreEnd

            $method = strtolower($params['method']);
            if ($method === 'auth.expiresession' ||
                $method === 'auth.revokeauthorization') {
                $this->destroySession();
            }

            return $result;
        }

        /**
         * Return true if this is video post.
         *
         * @param string $path The path
         * @param string $method The http method (default 'GET')
         *
         * @return boolean true if this is video post
         */
        protected function isVideoPost($path, $method = 'GET') {
            if ($method == 'POST' && preg_match("/^(\/)(.+)(\/)(videos)$/", $path)) {
                return true;
            }
            return false;
        }

        /**
         * Invoke the Graph API.
         *
         * @param string $path The path (required)
         * @param string $method The http method (default 'GET')
         * @param array $params The query/post data
         *
         * @return mixed The decoded response object
         * @throws FacebookApiException
         */
        protected function _graph($path, $method = 'GET', $params = array()) {
            if (is_array($method) && empty($params)) {
                $params = $method;
                $method = 'GET';
            }
            $params['method'] = $method; // method override as we always do a POST

            if ($this->isVideoPost($path, $method)) {
                $domainKey = 'graph_video';
            } else {
                $domainKey = 'graph';
            }

            $result = json_decode($this->_oauthRequest(
                $this->getUrl($domainKey, $path),
                $params
            ), true);

            // results are returned, errors are thrown
            if (is_array($result) && isset($result['error'])) {
                $this->throwAPIException($result);
                // @codeCoverageIgnoreStart
            }
            // @codeCoverageIgnoreEnd

            return $result;
        }

        /**
         * Make a OAuth Request.
         *
         * @param string $url The path (required)
         * @param array $params The query/post data
         *
         * @return string The decoded response object
         * @throws FacebookApiException
         */
        protected function _oauthRequest($url, $params) {
            if (!isset($params['access_token'])) {
                $params['access_token'] = $this->getAccessToken();
            }

            if (isset($params['access_token'])) {
                $params['appsecret_proof'] = $this->getAppSecretProof($params['access_token']);
            }

            // json_encode all params values that are not strings
            foreach ($params as $key => $value) {
                if (!is_string($value)) {
                    $params[$key] = json_encode($value);
                }
            }

            return $this->makeRequest($url, $params);
        }

        /**
         * Generate a proof of App Secret
         * This is required for all API calls originating from a server
         * It is a sha256 hash of the access_token made using the app secret
         *
         * @param string $access_token The access_token to be hashed (required)
         *
         * @return string The sha256 hash of the access_token
         */
        protected function getAppSecretProof($access_token) {
            return hash_hmac('sha256', $access_token, $this->getAppSecret());
        }

        /**
         * Makes an HTTP request. This method can be overridden by subclasses if
         * developers want to do fancier things or use something other than curl to
         * make the request.
         *
         * @param string $url The URL to make the request to
         * @param array $params The parameters to use for the POST body
         * @param CurlHandler $ch Initialized curl handle
         *
         * @return string The response text
         */
        protected function makeRequest($url, $params, $ch=null) {
            if (!$ch) {
                $ch = curl_init();
            }

            $opts = self::$CURL_OPTS;
            if ($this->getFileUploadSupport()) {
                $opts[CURLOPT_POSTFIELDS] = $params;
            } else {
                $opts[CURLOPT_POSTFIELDS] = http_build_query($params, null, '&');
            }
            $opts[CURLOPT_URL] = $url;

            // disable the 'Expect: 100-continue' behaviour. This causes CURL to wait
            // for 2 seconds if the server does not support this header.
            if (isset($opts[CURLOPT_HTTPHEADER])) {
                $existing_headers = $opts[CURLOPT_HTTPHEADER];
                $existing_headers[] = 'Expect:';
                $opts[CURLOPT_HTTPHEADER] = $existing_headers;
            } else {
                $opts[CURLOPT_HTTPHEADER] = array('Expect:');
            }

            curl_setopt_array($ch, $opts);
            $result = curl_exec($ch);

            if (curl_errno($ch) == 60) { // CURLE_SSL_CACERT
                self::errorLog('Invalid or no certificate authority found, '.
                    'using bundled information');
                curl_setopt($ch, CURLOPT_CAINFO,
                    dirname(__FILE__) . '/fb_ca_chain_bundle.crt');
                $result = curl_exec($ch);
            }

            // With dual stacked DNS responses, it's possible for a server to
            // have IPv6 enabled but not have IPv6 connectivity.  If this is
            // the case, curl will try IPv4 first and if that fails, then it will
            // fall back to IPv6 and the error EHOSTUNREACH is returned by the
            // operating system.
            if ($result === false && empty($opts[CURLOPT_IPRESOLVE])) {
                $matches = array();
                $regex = '/Failed to connect to ([^:].*): Network is unreachable/';
                if (preg_match($regex, curl_error($ch), $matches)) {
                    if (strlen(@inet_pton($matches[1])) === 16) {
                        self::errorLog('Invalid IPv6 configuration on server, '.
                            'Please disable or get native IPv6 on your server.');
                        self::$CURL_OPTS[CURLOPT_IPRESOLVE] = CURL_IPRESOLVE_V4;
                        curl_setopt($ch, CURLOPT_IPRESOLVE, CURL_IPRESOLVE_V4);
                        $result = curl_exec($ch);
                    }
                }
            }

            if ($result === false) {
                $e = new FacebookApiException(array(
                    'error_code' => curl_errno($ch),
                    'error' => array(
                        'message' => curl_error($ch),
                        'type' => 'CurlException',
                    ),
                ));
                curl_close($ch);
                throw $e;
            }
            curl_close($ch);
            return $result;
        }

        /**
         * Parses a signed_request and validates the signature.
         *
         * @param string $signed_request A signed token
         * @return array The payload inside it or null if the sig is wrong
         */
        protected function parseSignedRequest($signed_request) {
            list($encoded_sig, $payload) = explode('.', $signed_request, 2);

            // decode the data
            $sig = self::base64UrlDecode($encoded_sig);
            $data = json_decode(self::base64UrlDecode($payload), true);

            if (strtoupper($data['algorithm']) !== self::SIGNED_REQUEST_ALGORITHM) {
                self::errorLog(
                    'Unknown algorithm. Expected ' . self::SIGNED_REQUEST_ALGORITHM);
                return null;
            }

            // check sig
            $expected_sig = hash_hmac('sha256', $payload,
                $this->getAppSecret(), $raw = true);
            if ($sig !== $expected_sig) {
                self::errorLog('Bad Signed JSON signature!');
                return null;
            }

            return $data;
        }

        /**
         * Makes a signed_request blob using the given data.
         *
         * @param array The data array.
         * @return string The signed request.
         */
        protected function makeSignedRequest($data) {
            if (!is_array($data)) {
                throw new InvalidArgumentException(
                    'makeSignedRequest expects an array. Got: ' . print_r($data, true));
            }
            $data['algorithm'] = self::SIGNED_REQUEST_ALGORITHM;
            $data['issued_at'] = time();
            $json = json_encode($data);
            $b64 = self::base64UrlEncode($json);
            $raw_sig = hash_hmac('sha256', $b64, $this->getAppSecret(), $raw = true);
            $sig = self::base64UrlEncode($raw_sig);
            return $sig.'.'.$b64;
        }

