				$cat = $this->get_terms( $this->args->ID, 'category' );
				if ( $cat !== '' ) {
					$replacement = $cat;
				}
			}

			if ( ( ! isset( $replacement ) || $replacement === '' ) && ( isset( $this->args->cat_name ) && ! empty( $this->args->cat_name ) ) ) {
				$replacement = $this->args->cat_name;
